package demo;

public class Addition 
{
	public static void main(String[] args)
	{
		//TODO Auto-generated method stub
		System.out.println("Arithmetic Operators in Java");
		System.out.println("Addition " + (6 + 7));
		System.out.println("Substraction " + (6 - 7));
		System.out.println("Multiplication " + (6 * 7));
		System.out.println("Division " + (6 / 7));
		System.out.println("Modulus " + (6 % 7));
	}
}
